mapper.select:
SELECT tl.login_log_seq logSeq , tl.login_log_code loginCode , tl.login_log_reg_date regDate , tl.login_log_ip loginIp , tl.user_seq userSeq , tu.user_name userName , tu.user_id userId , tu.user_auth userAuth , tu.user_status userStatus FROM tbl_login_log tl INNER JOIN tbl_user tu ON (tl.user_seq = tu.user_seq) WHERE 0=0
AND tu.user_id = #{userId}
AND tu.user_name = #{userName}
AND tu.user_auth = #{userAuth}
AND tl.login_log_code = #{loginCode}
tl.login_log_reg_date >= to_date(#{startDate},'%Y%m%d%H%i%s')
tl.login_log_reg_date <= to_date(#{endDate},'%Y%m%d%H%i%s')
AND tl.login_log_reg_date BETWEEN to_date(#{startDate},'%Y%m%d%H%i%s') AND to_date(#{endDate},'%Y%m%d%H%i%s')
